import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showAuth, setShowAuth] = useState(false);
  const [user, setUser] = useState(null);
  const [newPost, setNewPost] = useState({
    title: '',
    content: '',
    mood: 'triste',
    author: 'Anonyme'
  });
  const [creating, setCreating] = useState(false);

  // üéØ D√âTECTION DES MOTS D'URGENCE
  const emergencyWords = ['suicide', 'mourir', 'suicider', 'mort', 'je veux mourir', 'je veux me tuer', 'finir ma vie'];

  const hasEmergencyWords = (text) => {
    const lowerText = text.toLowerCase();
    return emergencyWords.some(word => lowerText.includes(word));
  };

  // üéØ CHARGER LES POSTS DEPUIS L'API
  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/posts');

      if (!response.ok) {
        throw new Error('Erreur de chargement des publications');
      }

      const data = await response.json();

      if (data.success) {
        setPosts(data.posts);
      } else {
        throw new Error(data.message || 'Erreur inconnue');
      }

    } catch (error) {
      console.error('Erreur:', error);
      setError('Impossible de charger les publications');
    } finally {
      setLoading(false);
    }
  };

  // üéØ CR√âER UN NOUVEAU POST
  const handleCreatePost = async (e) => {
    e.preventDefault();

    if (!newPost.title.trim() || !newPost.content.trim()) {
      alert('Veuillez remplir le titre et le contenu');
      return;
    }

    setCreating(true);

    try {
      const response = await fetch('http://localhost:5000/api/posts', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newPost)
      });

      const data = await response.json();

      if (data.success) {
        // Ajouter la nouvelle publication √† la liste
        setPosts([data.post, ...posts]);
        // R√©initialiser le formulaire
        setNewPost({
          title: '',
          content: '',
          mood: 'triste',
          author: 'Anonyme'
        });
        // Fermer le formulaire
        setShowCreateForm(false);
        // Message de succ√®s
        alert('‚úÖ ' + data.message);

        // üö® V√âRIFIER LES MOTS D'URGENCE
        if (hasEmergencyWords(newPost.content)) {
          alert('üö® URGENCE : Si vous avez des pens√©es suicidaires, contactez imm√©diatement le +509 4005-7183 ou WhatsApp +509 4903-3260');
        }
      } else {
        alert('‚ùå ' + data.message);
      }
    } catch (error) {
      console.error('Erreur:', error);
      alert('‚ùå Erreur lors de la cr√©ation de la publication');
    } finally {
      setCreating(false);
    }
  };

  // üéØ SOUTENIR UN POST
  const handleSupport = async (postId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/posts/${postId}/support`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        }
      });

      const data = await response.json();

      if (data.success) {
        setPosts(posts.map(post => 
          post.id === postId 
            ? { ...post, supportCount: data.supportCount }
            : post
        ));

        alert('ü§ó ' + data.message);
      } else {
        alert('‚ùå ' + data.message);
      }
    } catch (error) {
      console.error('Erreur:', error);
      alert('‚ùå Erreur lors de l\'envoi du soutien');
    }
  };

  // üéØ AUTHENTIFICATION SIMPLIFI√âE
  const handleLogin = (userData) => {
    setUser(userData);
    setShowAuth(false);
    alert(`‚úÖ Bienvenue ${userData.username} !`);
  };

  const handleLogout = () => {
    setUser(null);
    alert('üëã √Ä bient√¥t !');
  };

  const getMoodEmoji = (mood) => {
    const emojis = {
      triste: 'üòî',
      anxieux: 'üò∞',
      espoir: '‚ú®',
      calme: 'üòå',
      col√®re: 'üò†',
      m√©lancolique: 'üåßÔ∏è'
    };
    return emojis[mood] || 'üòä';
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('fr-FR', {
      day: 'numeric',
      month: 'short',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="loading-screen">
        <div className="spinner">‚è≥</div>
        <h2>Chargement en cours</h2>
        <p>Connexion √† l'API en cours...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-screen">
        <div className="error-icon">‚ùå</div>
        <h2>Erreur de connexion</h2>
        <p>{error}</p>
        <button onClick={fetchPosts} className="btn-retry">
          R√©essayer
        </button>
      </div>
    );
  }

  return (
    <div className="App">
      {/* üéØ HEADER AVEC AUTHENTIFICATION */}
      <header className="header">
        <div className="container">
          <div className="header-content">
            <div className="logo">
              <span className="logo-emoji">ü§ó</span>
              <h1>Soutien Ensemble</h1>
            </div>

            <div className="header-actions">
              {user ? (
                <div className="user-info">
                  <span>üëã Bonjour, {user.username}</span>
                  <button onClick={handleLogout} className="btn-logout">
                    D√©connexion
                  </button>
                </div>
              ) : (
                <button onClick={() => setShowAuth(true)} className="btn-login">
                  üîê Connexion
                </button>
              )}
            </div>
          </div>
          <p className="tagline">Vous n'√™tes pas seul ‚Ä¢ Urgence: +509 4005-7183</p>
        </div>
      </header>

      {/* üéØ SECTION URGENCE TOUJOURS VISIBLE */}
      <section className="emergency-banner">
        <div className="container">
          <div className="emergency-content">
            <span className="emergency-icon">üö®</span>
            <div className="emergency-info">
              <strong>Besoin d'aide imm√©diate ?</strong>
              <span>Appelez le <a href="tel:+50940057183">+509 4005-7183</a> ou </span>
              <span>WhatsApp: <a href="https://wa.me/50949033260" target="_blank" rel="noopener noreferrer">+509 4903-3260</a></span>
            </div>
          </div>
        </div>
      </section>

      {/* üéØ HERO SECTION */}
      <section className="hero">
        <div className="container">
          <div className="hero-content">
            <h2>Espace d'expression et de soutien</h2>
            <p>Partagez vos sentiments et recevez du soutien dans un environnement bienveillant</p>

            {/* BOUTON CR√âER OU SE CONNECTER */}
            {user ? (
              <button 
                onClick={() => setShowCreateForm(true)}
                className="btn-create"
              >
                ‚úçÔ∏è Cr√©er une publication
              </button>
            ) : (
              <button 
                onClick={() => setShowAuth(true)}
                className="btn-create"
              >
                üîê Se connecter pour participer
              </button>
            )}

            <div className="hero-stats">
              <div className="stat">
                <span className="stat-number">{posts.length}</span>
                <span className="stat-label">Publications</span>
              </div>
              <div className="stat">
                <span className="stat-number">
                  {posts.reduce((sum, post) => sum + post.supportCount, 0)}
                </span>
                <span className="stat-label">Soutiens</span>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* üéØ FORMULAIRE DE CR√âATION */}
      {showCreateForm && (
        <section className="create-post-section">
          <div className="container">
            <div className="create-post-card">
              <div className="form-header">
                <h3>‚úçÔ∏è Partager mes sentiments</h3>
                <button onClick={() => setShowCreateForm(false)} className="btn-close">√ó</button>
              </div>

              <form onSubmit={handleCreatePost}>
                <div className="form-group">
                  <label>Titre *</label>
                  <input
                    type="text"
                    value={newPost.title}
                    onChange={(e) => setNewPost({...newPost, title: e.target.value})}
                    placeholder="Comment vous sentez-vous aujourd'hui ?"
                    maxLength="100"
                    required
                  />
                  <small>{newPost.title.length}/100 caract√®res</small>
                </div>

                <div className="form-group">
                  <label>Votre message *</label>
                  <textarea
                    value={newPost.content}
                    onChange={(e) => setNewPost({...newPost, content: e.target.value})}
                    placeholder="D√©crivez vos sentiments, vos pens√©es, ce que vous traversez..."
                    rows="6"
                    maxLength="1000"
                    required
                  />
                  <small>{newPost.content.length}/1000 caract√®res</small>
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label>Humeur</label>
                    <select
                      value={newPost.mood}
                      onChange={(e) => setNewPost({...newPost, mood: e.target.value})}
                    >
                      <option value="triste">üòî Triste</option>
