import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showAuth, setShowAuth] = useState(false);
  const [user, setUser] = useState(null);
  const [showComments, setShowComments] = useState({});
  const [comments, setComments] = useState({});
  const [newComment, setNewComment] = useState({});
  const [supportMessage, setSupportMessage] = useState('');
  const [currentMessageIndex, setCurrentMessageIndex] = useState(0);

  // Messages de bienvenue et motivation
  const welcomeMessages = [
    "üåü Bienvenue ! Votre bien-√™tre est notre priorit√©",
    "üí´ Vous avez fait le premier pas en venant ici, bravo !",
    "ü§ó Prenez votre temps, cet espace est fait pour vous",
    "üåª Chaque partage est une graine d'espoir plant√©e",
    "üïäÔ∏è Ici, vous pouvez √™tre vous-m√™me, sans jugement",
    "üåà Les nuages passent, mais le soleil reste toujours",
    "üéØ Vous n'√™tes pas seul dans ce cheminement",
    "üíù Votre courage nous inspire chaque jour",
    "üöÄ Le simple fait d'√™tre ici montre votre force int√©rieure",
    "üå∫ Votre histoire m√©rite d'√™tre entendue et respect√©e"
  ];

  // Charger les posts
  useEffect(() => {
    fetchPosts();
    fetchSupportMessage();
  }, []);

  const fetchPosts = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/posts');
      const data = await response.json();
      if (data.success) setPosts(data.posts);
    } catch (error) {
      setError('Impossible de charger les publications');
    } finally {
      setLoading(false);
    }
  };

  const fetchSupportMessage = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/support-message');
      const data = await response.json();
      if (data.success) setSupportMessage(data.message);
    } catch (error) {
      console.error('Erreur message de soutien:', error);
    }
  };

  const nextSupportMessage = async () => {
    await fetchSupportMessage();
  };

  const nextWelcomeMessage = () => {
    setCurrentMessageIndex((prev) => (prev + 1) % welcomeMessages.length);
  };

  const fetchComments = async (postId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/posts/${postId}/comments`);
      const data = await response.json();
      if (data.success) {
        setComments(prev => ({ ...prev, [postId]: data.comments }));
      }
    } catch (error) {
      console.error('Erreur chargement commentaires:', error);
    }
  };

  const handleSupport = async (postId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/posts/${postId}/support`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' }
      });
      const data = await response.json();
      if (data.success) {
        setPosts(posts.map(post => 
          post.id === postId ? { ...post, supportCount: data.supportCount } : post
        ));
        alert('ü§ó ' + data.message);
      }
    } catch (error) {
      console.error('Erreur soutien:', error);
    }
  };

  const handleCreatePost = async (formData) => {
    try {
      const response = await fetch('http://localhost:5000/api/posts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      if (data.success) {
        fetchPosts();
        setShowCreateForm(false);
        alert(data.message);
      }
    } catch (error) {
      console.error('Erreur cr√©ation post:', error);
    }
  };

  const handleComment = async (postId) => {
    if (!newComment[postId]?.trim()) {
      alert('Veuillez √©crire un commentaire');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/comments', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          postId,
          content: newComment[postId],
          author: user?.username || 'Visiteur'  // Utilise "Visiteur" si pas connect√©
        })
      });
      const data = await response.json();
      if (data.success) {
        setNewComment(prev => ({ ...prev, [postId]: '' }));
        fetchComments(postId);
        alert('üí¨ ' + data.message);
      }
    } catch (error) {
      console.error('Erreur commentaire:', error);
    }
  };

  const toggleComments = (postId) => {
    setShowComments(prev => ({ ...prev, [postId]: !prev[postId] }));
    if (!comments[postId]) fetchComments(postId);
  };

  const handleLogin = (userData) => {
    setUser(userData);
    setShowAuth(false);
    alert(`üéâ Bienvenue ${userData.username} ! Merci de rejoindre notre communaut√©.`);
  };

  if (loading) return <LoadingScreen />;
  if (error) return <ErrorScreen error={error} onRetry={fetchPosts} />;

  return (
    <div className="App">
      <Header user={user} onAuth={() => setShowAuth(true)} onLogout={() => setUser(null)} />

      <HeroSection 
        posts={posts} 
        supportMessage={supportMessage}
        welcomeMessage={welcomeMessages[currentMessageIndex]}
        onShowCreateForm={() => setShowCreateForm(true)}
        onNextSupportMessage={nextSupportMessage}
        onNextWelcomeMessage={nextWelcomeMessage}
        user={user}
      />

      {showCreateForm && (
        <CreatePostForm 
          onSubmit={handleCreatePost}
          onClose={() => setShowCreateForm(false)}
          user={user}
        />
      )}

      {showAuth && (
        <AuthModal 
          onLogin={handleLogin}
          onClose={() => setShowAuth(false)}
        />
      )}

      <EncouragementSection />

      <PostsSection 
        posts={posts}
        showComments={showComments}
        comments={comments}
        newComment={newComment}
        onSupport={handleSupport}
        onToggleComments={toggleComments}
        onCommentChange={setNewComment}
        onCommentSubmit={handleComment}
        user={user}
      />

      <EmergencySection />

      <Footer />
    </div>
  );
}

// Composant AuthModal simplifi√©
function AuthModal({ onLogin, onClose }) {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    username: '', email: '', password: '', confirmPassword: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simulation de connexion
    const userData = {
      id: 1,
      username: formData.username || 'Utilisateur',
      email: formData.email,
      role: 'user'
    };
    onLogin(userData);
  };

  return (
    <div className="auth-modal">
      <div className="auth-content">
        <div className="auth-header">
          <h2>{isLogin ? 'Connexion' : 'Inscription'}</h2>
          <button onClick={onClose} className="btn-close">√ó</button>
        </div>

        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <div className="form-group">
              <label>Nom d'utilisateur</label>
              <input
                type="text"
                value={formData.username}
                onChange={(e) => setFormData({...formData, username: e.target.value})}
                placeholder="Choisissez un nom"
              />
            </div>
          )}

          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) => setFormData({...formData, email: e.target.value})}
              placeholder="votre@email.com"
            />
          </div>

          <div className="form-group">
            <label>Mot de passe</label>
            <input
              type="password"
              value={formData.password}
              onChange={(e) => setFormData({...formData, password: e.target.value})}
              placeholder="Votre mot de passe"
            />
          </div>

          <button type="submit" className="btn-auth">
            {isLogin ? 'Se connecter' : 'Cr√©er un compte'}
          </button>
        </form>

        <div className="auth-switch">
          <button onClick={() => setIsLogin(!isLogin)} className="btn-switch">
            {isLogin ? "Cr√©er un compte" : "D√©j√† un compte ? Se connecter"}
          </button>
        </div>
      </div>
    </div>
  );
}

// Autres composants restent similaires mais avec am√©liorations...
// [Je garde les autres composants similaires mais avec les corrections]

function HeroSection({ posts, supportMessage, welcomeMessage, onShowCreateForm, onNextSupportMessage, onNextWelcomeMessage, user }) {
  return (
    <section className="hero">
      <div className="container">
        <div className="hero-content">
          <div className="welcome-message">
            <h2>ü§ó Soutien Ensemble</h2>
            <p className="welcome-text">{welcomeMessage}</p>
            <button onClick={onNextWelcomeMessage} className="btn-next-message">
              üí´ Message suivant
            </button>
          </div>

          {supportMessage && (
            <div className="support-message-card">
              <div className="message-content">
                <span className="message-icon">üíù</span>
                {supportMessage}
              </div>
              <button onClick={onNextSupportMessage} className="btn-next-support">
                üîÑ Autre message
              </button>
            </div>
          )}

          <div className="action-cards">
            <div className="action-card" onClick={onShowCreateForm}>
              <div className="action-icon">‚úçÔ∏è</div>
              <h3>Exprimez-vous</h3>
              <p>Partagez vos sentiments en toute confidentialit√©</p>
              <button className="btn-action">
                {user ? '√âcrire' : 'Commencer'}
              </button>
            </div>

            <div className="action-card">
              <div className="action-icon">ü§ó</div>
              <h3>Soutenez</h3>
              <p>Envoyez des messages d'encouragement</p>
              <button className="btn-action">
                Soutenir
              </button>
            </div>

            <div className="action-card">
              <div className="action-icon">üí¨</div>
              <h3>√âchangez</h3>
              <p>Rejoignez les conversations bienveillantes</p>
              <button className="btn-action">
                Participer
              </button>
            </div>
          </div>

          <div className="hero-stats">
            <div className="stat">
              <span className="stat-number">{posts.length}</span>
              <span className="stat-label">Publications</span>
            </div>
            <div className="stat">
              <span className="stat-number">
                {posts.reduce((sum, post) => sum + post.supportCount, 0)}
              </span>
              <span className="stat-label">Soutiens</span>
            </div>
            <div className="stat">
              <span className="stat-number">
                {Object.values(comments).flat().length}
              </span>
              <span className="stat-label">Commentaires</span>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

function EncouragementSection() {
  return (
    <section className="encouragement-section">
      <div className="container">
        <div className="encouragement-grid">
          <div className="encouragement-card">
            <div className="encouragement-icon">üåü</div>
            <h4>Votre voix compte</h4>
            <p>Chaque partage aide quelqu'un √† se sentir moins seul</p>
          </div>
          <div className="encouragement-card">
            <div className="encouragement-icon">üí™</div>
            <h4>Vous √™tes fort</h4>
            <p>Le simple fait d'√™tre ici montre votre courage</p>
          </div>
          <div className="encouragement-card">
            <div className="encouragement-icon">üåà</div>
            <h4>Des jours meilleurs</h4>
            <p>Les nuages finissent toujours par passer</p>
          </div>
        </div>
      </div>
    </section>
  );
}

// [Les autres composants PostsSection, EmergencySection, etc. restent similaires avec les corrections des commentaires]

export default App;
