import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showAuth, setShowAuth] = useState(false);
  const [user, setUser] = useState(null);
  const [showComments, setShowComments] = useState({});
  const [comments, setComments] = useState({});
  const [newComment, setNewComment] = useState({});
  const [supportMessage, setSupportMessage] = useState('');
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Images motivantes sans photos externes
  const motivationalImages = [
    { emoji: "üåÖ", text: "Nouveau jour, nouvelles opportunit√©s" },
    { emoji: "üåà", text: "Apr√®s la pluie vient le beau temps" },
    { emoji: "üí™", text: "Vous √™tes plus fort que vous ne le pensez" },
    { emoji: "‚ú®", text: "Votre lumi√®re int√©rieure brille toujours" },
    { emoji: "üå±", text: "Chaque jour est une nouvelle croissance" },
    { emoji: "ü¶ã", text: "La transformation est possible" }
  ];

  // Messages de bienvenue motivants
  const welcomeMessages = [
    "Bienvenue dans votre espace de paix ü§ó",
    "Ici, vous pouvez √™tre vous-m√™me en toute s√©curit√© üåü", 
    "Votre bien-√™tre est notre priorit√© üí´",
    "Prenez un moment pour respirer et partager üå¨Ô∏è",
    "Vous n'√™tes jamais seul dans vos combats ü§ù",
    "Chaque partage est un pas vers la gu√©rison üë£"
  ];

  useEffect(() => {
    fetchPosts();
    fetchSupportMessage();
  }, []);

  const fetchPosts = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/posts');
      const data = await response.json();
      if (data.success) setPosts(data.posts);
    } catch (error) {
      setError('Impossible de charger les publications');
    } finally {
      setLoading(false);
    }
  };

  const fetchSupportMessage = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/support-message');
      const data = await response.json();
      if (data.success) setSupportMessage(data.message);
    } catch (error) {
      // Message de secours
      setSupportMessage("Prenez soin de vous, vous le m√©ritez üåü");
    }
  };

  const fetchComments = async (postId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/posts/${postId}/comments`);
      const data = await response.json();
      if (data.success) {
        setComments(prev => ({ ...prev, [postId]: data.comments }));
      }
    } catch (error) {
      console.error('Erreur chargement commentaires:', error);
    }
  };

  const handleSupport = async (postId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/posts/${postId}/support`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' }
      });
      const data = await response.json();
      if (data.success) {
        setPosts(posts.map(post => 
          post.id === postId ? { ...post, supportCount: data.supportCount } : post
        ));
        alert('ü§ó ' + data.message);
      }
    } catch (error) {
      console.error('Erreur soutien:', error);
    }
  };

  const handleCreatePost = async (formData) => {
    try {
      const response = await fetch('http://localhost:5000/api/posts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      if (data.success) {
        fetchPosts();
        setShowCreateForm(false);
        alert(data.message);
      }
    } catch (error) {
      console.error('Erreur cr√©ation post:', error);
    }
  };

  const handleComment = async (postId) => {
    if (!newComment[postId]?.trim()) {
      alert('Veuillez √©crire un commentaire');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/comments', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          postId,
          content: newComment[postId],
          author: user?.username || 'Anonyme'
        })
      });
      const data = await response.json();
      if (data.success) {
        setNewComment(prev => ({ ...prev, [postId]: '' }));
        fetchComments(postId);
        alert('üí¨ Commentaire publi√© !');
      }
    } catch (error) {
      console.error('Erreur commentaire:', error);
    }
  };

  const toggleComments = (postId) => {
    if (!user) {
      alert('üîê Veuillez vous connecter pour commenter');
      setShowAuth(true);
      return;
    }
    setShowComments(prev => ({ ...prev, [postId]: !prev[postId] }));
    if (!comments[postId]) fetchComments(postId);
  };

  const nextMotivationalImage = () => {
    setCurrentImageIndex((prev) => (prev + 1) % motivationalImages.length);
  };

  const handleLogin = (userData) => {
    setUser(userData);
    setShowAuth(false);
    alert(`üéâ Bienvenue ${userData.username} !`);
  };

  const getMoodEmoji = (mood) => {
    const emojis = { 
      triste: 'üòî', 
      anxieux: 'üò∞', 
      espoir: '‚ú®', 
      calme: 'üòå',
      col√®re: 'üò†',
      m√©lancolique: 'üåßÔ∏è',
      fatigu√©: 'üò¥',
      confus: 'üòµ'
    };
    return emojis[mood] || 'üòä';
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('fr-FR', {
      day: 'numeric', month: 'short', hour: '2-digit', minute: '2-digit'
    });
  };

  // √âcran de chargement
  if (loading) {
    return (
      <div className="loading-screen">
        <div className="spinner">‚è≥</div>
        <h2>Chargement de votre espace de soutien</h2>
        <p>Pr√©paration de votre environnement s√©curis√©...</p>
      </div>
    );
  }

  // √âcran d'erreur
  if (error) {
    return (
      <div className="error-screen">
        <div className="error-icon">‚ùå</div>
        <h2>Erreur de connexion</h2>
        <p>{error}</p>
        <button onClick={fetchPosts} className="btn-retry">R√©essayer</button>
        <div className="help-text">
          <p>üí° Assurez-vous que le serveur backend est d√©marr√© sur le port 5000</p>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      {/* Header avec connexion */}
      <header className="header">
        <div className="container">
          <div className="header-content">
            <div className="logo">
              <span className="logo-emoji">ü§ó</span>
              <h1>Soutien Ensemble</h1>
            </div>
            <div className="header-actions">
              {user ? (
                <div className="user-info">
                  <span className="welcome-user">üëã Bonjour, {user.username}</span>
                  <button onClick={() => setUser(null)} className="btn-logout">
                    D√©connexion
                  </button>
                </div>
              ) : (
                <button onClick={() => setShowAuth(true)} className="btn-login">
                  üîê Connexion
                </button>
              )}
            </div>
          </div>
          <p className="tagline">Vous n'√™tes pas seul ‚Ä¢ Espace s√©curis√© ‚Ä¢ √âcoute bienveillante</p>
        </div>
      </header>

      {/* Section Hero avec messages de bienvenue */}
      <section className="hero">
        <div className="container">
          <div className="hero-content">
            <div className="welcome-messages">
              <h2>{welcomeMessages[Math.floor(Math.random() * welcomeMessages.length)]}</h2>
              <p>Partagez vos sentiments en toute confidentialit√© et recevez du soutien</p>
            </div>

            {/* Images motivantes interactives */}
            <div className="motivational-display">
              <div className="motivational-card">
                <div className="motivational-emoji">{motivationalImages[currentImageIndex].emoji}</div>
                <p className="motivational-text">{motivationalImages[currentImageIndex].text}</p>
                <button onClick={nextMotivationalImage} className="btn-next-motivation">
                  ‚û°Ô∏è Message suivant
                </button>
              </div>
            </div>

            {/* Message de soutien al√©atoire */}
            {supportMessage && (
              <div className="support-message-bubble">
                <span className="bubble-emoji">üí´</span>
                <p>{supportMessage}</p>
                <button onClick={fetchSupportMessage} className="btn-new-message">
                  üîÑ Nouveau message
                </button>
              </div>
            )}

            {/* Bouton d'action principal */}
            <div className="hero-actions">
              {user ? (
                <button onClick={() => setShowCreateForm(true)} className="btn-create-main">
                  ‚úçÔ∏è Partager mes sentiments
                </button>
              ) : (
                <button onClick={() => setShowAuth(true)} className="btn-create-main">
                  üîê Se connecter pour partager
                </button>
              )}

              <button onClick={fetchPosts} className="btn-refresh-main">
                üîÑ Actualiser les publications
              </button>
            </div>

            {/* Statistiques */}
            <div className="hero-stats">
              <div className="stat">
                <span className="stat-number">{posts.length}</span>
                <span className="stat-label">Publications</span>
              </div>
              <div className="stat">
                <span className="stat-number">
                  {posts.reduce((sum, post) => sum + post.supportCount, 0)}
                </span>
                <span className="stat-label">Soutiens</span>
              </div>
              <div className="stat">
                <span className="stat-number">
                  {Object.values(comments).flat().length}
                </span>
                <span className="stat-label">Commentaires</span>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Formulaire de cr√©ation de post */}
      {showCreateForm && (
        <CreatePostForm 
          onSubmit={handleCreatePost}
          onClose={() => setShowCreateForm(false)}
          user={user}
        />
      )}

      {/* Formulaire d'authentification simplifi√© */}
      {showAuth && (
        <AuthForm 
          onLogin={handleLogin}
          onClose={() => setShowAuth(false)}
        />
      )}

      {/* Section des publications */}
      <section className="posts-section">
        <div className="container">
          <div className="section-header">
            <h3>üìù Derni√®res publications de la communaut√©</h3>
            <div className="section-actions">
              <button onClick={fetchPosts} className="btn-refresh">
                üîÑ Actualiser
              </button>
            </div>
          </div>

          <div className="posts-grid">
            {posts.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">üìù</div>
                <h4>Aucune publication pour le moment</h4>
                <p>Soyez le premier √† partager vos sentiments</p>
                {!user && (
                  <button onClick={() => setShowAuth(true)} className="btn-connect-invite">
                    üîê Se connecter pour commencer
                  </button>
                )}
              </div>
            ) : (
              posts.map(post => (
                <PostCard 
                  key={post.id}
                  post={post}
                  showComments={showComments[post.id]}
                  comments={comments[post.id] || []}
                  newComment={newComment[post.id] || ''}
                  user={user}
                  onSupport={() => handleSupport(post.id)}
                  onToggleComments={() => toggleComments(post.id)}
                  onCommentChange={(content) => setNewComment(prev => ({ ...prev, [post.id]: content }))}
                  onCommentSubmit={() => handleComment(post.id)}
                  getMoodEmoji={getMoodEmoji}
                  formatDate={formatDate}
                />
              ))
            )}
          </div>
        </div>
      </section>

      {/* Section d'urgence */}
      <EmergencySection />

      {/* Pied de page */}
      <Footer />
    </div>
  );
}

// Composant de formulaire de cr√©ation de post
function CreatePostForm({ onSubmit, onClose, user }) {
  const [formData, setFormData] = useState({
    title: '', 
    content: '', 
    mood: 'triste', 
    author: user ? 'Moi' : 'Anonyme'
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.title.trim() || !formData.content.trim()) {
      alert('Veuillez remplir le titre et le contenu');
      return;
    }
    onSubmit(formData);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="form-header">
          <h3>‚úçÔ∏è Partager mes sentiments</h3>
          <button onClick={onClose} className="btn-close">√ó</button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Titre *</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData({...formData, title: e.target.value})}
              placeholder="Comment vous sentez-vous aujourd'hui ?"
              required
            />
          </div>

          <div className="form-group">
            <label>Votre message *</label>
            <textarea
              value={formData.content}
              onChange={(e) => setFormData({...formData, content: e.target.value})}
              placeholder="D√©crivez vos sentiments, vos pens√©es, ce que vous traversez..."
              rows="6"
              required
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Humeur</label>
              <select
                value={formData.mood}
                onChange={(e) => setFormData({...formData, mood: e.target.value})}
              >
                <option value="triste">üòî Triste</option>
                <option value="anxieux">üò∞ Anxieux</option>
                <option value="col√®re">üò† Col√®re</option>
                <option value="calme">üòå Calme</option>
                <option value="espoir">‚ú® Plein d'espoir</option>
                <option value="m√©lancolique">üåßÔ∏è M√©lancolique</option>
                <option value="fatigu√©">üò¥ Fatigu√©</option>
                <option value="confus">üòµ Confus</option>
              </select>
            </div>

            <div className="form-group">
              <label>Publication</label>
              <select
                value={formData.author}
                onChange={(e) => setFormData({...formData, author: e.target.value})}
              >
                <option value="Anonyme">üïµÔ∏è Anonyme</option>
                <option value="Moi">üë§ Avec mon pseudo</option>
              </select>
            </div>
          </div>

          <div className="form-actions">
            <button type="button" onClick={onClose} className="btn-cancel">
              Annuler
            </button>
            <button type="submit" className="btn-submit">
              Publier ü§ó
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

// Composant d'authentification simplifi√©
function AuthForm({ onLogin, onClose }) {
  const [formData, setFormData] = useState({
    username: '', 
    email: '', 
    password: '',
    isLogin: true
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simulation de connexion
    const userData = {
      id: 1,
      username: formData.username || 'Utilisateur',
      email: formData.email,
      role: 'user'
    };
    onLogin(userData);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content auth-modal">
        <div className="form-header">
          <h3>{formData.isLogin ? 'üîê Connexion' : 'üë§ Cr√©er un compte'}</h3>
          <button onClick={onClose} className="btn-close">√ó</button>
        </div>

        <form onSubmit={handleSubmit}>
          {!formData.isLogin && (
            <div className="form-group">
              <label>Nom d'utilisateur</label>
              <input
                type="text"
                value={formData.username}
                onChange={(e) => setFormData({...formData, username: e.target.value})}
                placeholder="Choisissez un nom d'utilisateur"
              />
            </div>
          )}

          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) => setFormData({...formData, email: e.target.value})}
              placeholder="votre@email.com"
              required
            />
          </div>

          <div className="form-group">
            <label>Mot de passe</label>
            <input
              type="password"
              value={formData.password}
              onChange={(e) => setFormData({...formData, password: e.target.value})}
              placeholder="Votre mot de passe"
              required
            />
          </div>

          <button type="submit" className="btn-submit">
            {formData.isLogin ? 'Se connecter' : 'Cr√©er mon compte'}
          </button>
        </form>

        <div className="auth-switch">
          <p>
            {formData.isLogin ? "Pas de compte ? " : "D√©j√† un compte ? "}
            <button 
              type="button" 
              onClick={() => setFormData(prev => ({ ...prev, isLogin: !prev.isLogin }))}
              className="btn-switch"
            >
              {formData.isLogin ? "S'inscrire" : "Se connecter"}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
}

// Composant de carte de post
function PostCard({ 
  post, 
  showComments, 
  comments, 
  newComment, 
  user, 
  onSupport, 
  onToggleComments, 
  onCommentChange, 
  onCommentSubmit,
  getMoodEmoji,
  formatDate
}) {
  return (
    <div className={`post-card ${post.isEmergency ? 'emergency' : ''}`}>
      {post.isEmergency && (
        <div className="emergency-alert">
          üö® Message d√©tect√© comme urgent - Soutien imm√©diat disponible
        </div>
      )}

      <div className="post-header">
        <div className="post-author">
          <span className="author-avatar">
            {post.author === 'Anonyme' ? 'üïµÔ∏è' : 'üë§'}
          </span>
          <span className="author-name">{post.author}</span>
        </div>
        <div className="post-mood">
          <span className="mood-emoji">{getMoodEmoji(post.mood)}</span>
          <span className="mood-text">{post.mood}</span>
        </div>
      </div>

      <div className="post-content">
        <h4 className="post-title">{post.title}</h4>
        <p className="post-text">{post.content}</p>
      </div>

      <div className="post-footer">
        <div className="post-meta">
          <span className="post-date">{formatDate(post.createdAt)}</span>
        </div>
        <div className="post-actions">
          <button onClick={onSupport} className="btn-support">
            <span className="support-emoji">ü§ó</span>
            Soutenir ({post.supportCount})
          </button>
          <button onClick={onToggleComments} className="btn-comment">
            <span className="comment-emoji">üí¨</span>
            Commenter ({comments.length})
          </button>
        </div>
      </div>

      {showComments && (
        <div className="comments-section">
          <div className="comments-list">
            {comments.length === 0 ? (
              <div className="no-comments">
                <p>üí¨ Aucun commentaire pour le moment</p>
                <p>Soyez le premier √† soutenir cette personne</p>
              </div>
            ) : (
              comments.map(comment => (
                <div key={comment.id} className="comment">
                  <div className="comment-header">
                    <strong>{comment.author}</strong>
                    <span>{formatDate(comment.createdAt)}</span>
                  </div>
                  <p>{comment.content}</p>
                </div>
              ))
            )}
          </div>

          <div className="comment-form">
            <textarea
              value={newComment}
              onChange={(e) => onCommentChange(e.target.value)}
              placeholder="Votre message de soutien..."
              rows="3"
            />
            <button 
              onClick={onCommentSubmit}
              className="btn-comment-submit"
            >
              {user ? 'üí¨ Envoyer le commentaire' : 'üîê Connectez-vous pour commenter'}
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

