import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [newPost, setNewPost] = useState({
    title: '',
    content: '',
    mood: 'triste',
    author: 'Anonyme'
  });

  // üéØ CHARGER LES POSTS DEPUIS L'API
  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/posts');

      if (response.ok) {
        const data = await response.json();
        setPosts(data.posts);
      } else {
        throw new Error('API non disponible');
      }
    } catch (error) {
      console.log('Utilisation des donn√©es locales');
      // Donn√©es locales de secours
      setPosts([
        {
          id: 1,
          title: "Besoin de parler aujourd'hui",
          content: "Je me sens seul et j'ai besoin de partager ce que je ressens. Les journ√©es sont longues et difficiles en ce moment.",
          author: "Anonyme",
          mood: "triste",
          supportCount: 5,
          createdAt: new Date()
        },
        {
          id: 2,
          title: "Petite victoire personnelle",
          content: "Aujourd'hui j'ai r√©ussi √† sortir faire une petite marche. Apr√®s plusieurs jours enferm√©, cet air frais m'a fait beaucoup de bien.",
          author: "Marie",
          mood: "espoir",
          supportCount: 12,
          createdAt: new Date()
        },
        {
          id: 3,
          title: "Anxi√©t√© qui monte",
          content: "Les pens√©es tournent en boucle dans ma t√™te. J'aimerais pouvoir les arr√™ter mais c'est plus fort que moi ce soir.",
          author: "Anonyme",
          mood: "anxieux",
          supportCount: 8,
          createdAt: new Date()
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  // üéØ SOUTENIR UN POST
  const handleSupport = async (postId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/posts/${postId}/support`, {
        method: 'POST'
      });

      if (response.ok) {
        const data = await response.json();
        setPosts(posts.map(post => 
          post.id === postId 
            ? { ...post, supportCount: data.supportCount }
            : post
        ));
        alert('ü§ó ' + data.message);
      } else {
        throw new Error('API non disponible');
      }
    } catch (error) {
      // Fallback local
      setPosts(posts.map(post => 
        post.id === postId 
          ? { ...post, supportCount: post.supportCount + 1 }
          : post
      ));
      alert('ü§ó Soutien envoy√© !');
    }
  };

  // üéØ CR√âER UN NOUVEAU POST
  const handleCreatePost = async (e) => {
    e.preventDefault();

    if (!newPost.title.trim() || !newPost.content.trim()) {
      alert('Veuillez remplir le titre et le contenu');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/posts', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newPost)
      });

      if (response.ok) {
        const data = await response.json();
        setPosts([data.post, ...posts]);
        setNewPost({
          title: '',
          content: '',
          mood: 'triste',
          author: 'Anonyme'
        });
        setShowCreateForm(false);
        alert('‚úÖ ' + data.message);
      } else {
        throw new Error('API non disponible');
      }
    } catch (error) {
      // Fallback local
      const localPost = {
        id: posts.length + 1,
        ...newPost,
        supportCount: 0,
        createdAt: new Date()
      };
      setPosts([localPost, ...posts]);
      setNewPost({
        title: '',
        content: '',
        mood: 'triste',
        author: 'Anonyme'
      });
      setShowCreateForm(false);
      alert('‚úÖ Publication cr√©√©e localement !');
    }
  };

  const handleInputChange = (field, value) => {
    setNewPost(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const getMoodEmoji = (mood) => {
    const emojis = {
      triste: 'üòî',
      anxieux: 'üò∞',
      espoir: '‚ú®',
      calme: 'üòå',
      col√®re: 'üò†',
      m√©lancolique: 'üåßÔ∏è'
    };
    return emojis[mood] || 'üòä';
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('fr-FR', {
      day: 'numeric',
      month: 'short',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner">‚è≥</div>
        <h2>Chargement des publications...</h2>
      </div>
    );
  }

  return (
    <div className="App">
      {/* Header */}
      <header className="header">
        <div className="container">
          <div className="logo">
            <span className="logo-emoji">ü§ó</span>
            <h1>Soutien Ensemble</h1>
          </div>
          <p className="tagline">Vous n'√™tes pas seul</p>
        </div>
      </header>

      {/* Hero Section */}
      <section className="hero">
        <div className="container">
          <div className="hero-content">
            <h2>Espace d'expression et de soutien</h2>
            <p>Partagez vos sentiments et recevez du soutien dans un environnement bienveillant</p>

            <button 
              onClick={() => setShowCreateForm(true)}
              className="btn-create"
            >
              ‚úçÔ∏è Cr√©er une publication
            </button>

            <div className="hero-stats">
              <div className="stat">
                <span className="stat-number">{posts.length}</span>
                <span className="stat-label">Publications</span>
              </div>
              <div className="stat">
                <span className="stat-number">
                  {posts.reduce((sum, post) => sum + post.supportCount, 0)}
                </span>
                <span className="stat-label">Soutiens</span>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Formulaire de cr√©ation */}
      {showCreateForm && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>‚úçÔ∏è Partager mes sentiments</h3>
              <button 
                onClick={() => setShowCreateForm(false)}
                className="btn-close"
              >
                √ó
              </button>
            </div>

            <form onSubmit={handleCreatePost}>
              <div className="form-group">
                <label>Titre *</label>
                <input
                  type="text"
                  value={newPost.title}
                  onChange={(e) => handleInputChange('title', e.target.value)}
                  placeholder="Comment vous sentez-vous aujourd'hui ?"
                  required
                />
              </div>

              <div className="form-group">
                <label>Votre message *</label>
                <textarea
                  value={newPost.content}
                  onChange={(e) => handleInputChange('content', e.target.value)}
                  placeholder="D√©crivez vos sentiments, vos pens√©es..."
                  rows="4"
                  required
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Humeur</label>
                  <select
                    value={newPost.mood}
                    onChange={(e) => handleInputChange('mood', e.target.value)}
                  >
                    <option value="triste">üòî Triste</option>
                    <option value="anxieux">üò∞ Anxieux</option>
                    <option value="col√®re">üò† Col√®re</option>
                    <option value="calme">üòå Calme</option>
                    <option value="espoir">‚ú® Espoir</option>
                  </select>
                </div>

                <div className="form-group">
                  <label>Publication</label>
                  <select
                    value={newPost.author}
                    onChange={(e) => handleInputChange('author', e.target.value)}
                  >
                    <option value="Anonyme">üïµÔ∏è Anonyme</option>
                    <option value="Moi">üë§ Avec mon pseudo</option>
                  </select>
                </div>
              </div>

              <div className="form-actions">
                <button 
                  type="button"
                  onClick={() => setShowCreateForm(false)}
                  className="btn-cancel"
                >
                  Annuler
                </button>
                <button type="submit" className="btn-submit">
                  Publier ü§ó
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Posts Section */}
      <section className="posts-section">
        <div className="container">
          <div className="section-header">
            <h3>Derni√®res publications</h3>
            <button className="btn-refresh" onClick={fetchPosts}>
              üîÑ Actualiser
            </button>
          </div>

          <div className="posts-grid">
            {posts.map(post => (
              <div key={post.id} className="post-card">
                <div className="post-header">
                  <div className="post-author">
                    <span className="author-avatar">
                      {post.author === 'Anonyme' ? 'üïµÔ∏è' : 'üë§'}
                    </span>
                    <span className="author-name">{post.author}</span>
                  </div>
                  <div className="post-mood">
                    <span className="mood-emoji">{getMoodEmoji(post.mood)}</span>
                    <span className="mood-text">{post.mood}</span>
                  </div>
                </div>

                <div className="post-content">
                  <h4 className="post-title">{post.title}</h4>
                  <p className="post-text">{post.content}</p>
                </div>

                <div className="post-footer">
                  <div className="post-meta">
                    <span className="post-date">
                      {formatDate(post.createdAt)}
                    </span>
                  </div>
                  <div className="post-actions">
                    <button 
                      onClick={() => handleSupport(post.id)}
                      className="btn-support"
                    >
                      <span className="support-emoji">ü§ó</span>
                      Soutenir ({post.supportCount})
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      <footer className="footer">
        <div className="container">
          <p>ü§ó Soutien Ensemble - Plateforme compl√®te et fonctionnelle</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
