import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showAuth, setShowAuth] = useState(false);
  const [user, setUser] = useState(null);
  const [showComments, setShowComments] = useState({});
  const [comments, setComments] = useState({});
  const [newComment, setNewComment] = useState({});
  const [supportMessage, setSupportMessage] = useState('');
  const [motivationalImage, setMotivationalImage] = useState('');

  // Charger les posts
  useEffect(() => {
    fetchPosts();
    fetchSupportMessage();
    fetchMotivationalImage();
  }, []);

  const fetchPosts = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/posts');
      const data = await response.json();
      if (data.success) setPosts(data.posts);
    } catch (error) {
      setError('Impossible de charger les publications');
    } finally {
      setLoading(false);
    }
  };

  const fetchSupportMessage = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/support-message');
      const data = await response.json();
      if (data.success) setSupportMessage(data.message);
    } catch (error) {
      console.error('Erreur message de soutien:', error);
    }
  };

  const fetchMotivationalImage = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/motivational-image');
      const data = await response.json();
      if (data.success) setMotivationalImage(data.image);
    } catch (error) {
      console.error('Erreur image motivante:', error);
    }
  };

  const fetchComments = async (postId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/posts/${postId}/comments`);
      const data = await response.json();
      if (data.success) {
        setComments(prev => ({ ...prev, [postId]: data.comments }));
      }
    } catch (error) {
      console.error('Erreur chargement commentaires:', error);
    }
  };

  const handleSupport = async (postId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/posts/${postId}/support`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' }
      });
      const data = await response.json();
      if (data.success) {
        setPosts(posts.map(post => 
          post.id === postId ? { ...post, supportCount: data.supportCount } : post
        ));
      }
    } catch (error) {
      console.error('Erreur soutien:', error);
    }
  };

  const handleCreatePost = async (formData) => {
    try {
      const response = await fetch('http://localhost:5000/api/posts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      if (data.success) {
        fetchPosts();
        setShowCreateForm(false);
        alert(data.message);
      }
    } catch (error) {
      console.error('Erreur cr√©ation post:', error);
    }
  };

  const handleComment = async (postId) => {
    if (!newComment[postId]?.trim()) return;

    try {
      const response = await fetch('http://localhost:5000/api/comments', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          postId,
          content: newComment[postId],
          author: user?.username || 'Anonyme'
        })
      });
      const data = await response.json();
      if (data.success) {
        setNewComment(prev => ({ ...prev, [postId]: '' }));
        fetchComments(postId);
      }
    } catch (error) {
      console.error('Erreur commentaire:', error);
    }
  };

  const toggleComments = (postId) => {
    setShowComments(prev => ({ ...prev, [postId]: !prev[postId] }));
    if (!comments[postId]) fetchComments(postId);
  };

  const getMoodEmoji = (mood) => {
    const emojis = { triste: 'üòî', anxieux: 'üò∞', espoir: '‚ú®', calme: 'üòå', col√®re: 'üò†' };
    return emojis[mood] || 'üòä';
  };

  if (loading) return <LoadingScreen />;
  if (error) return <ErrorScreen error={error} onRetry={fetchPosts} />;

  return (
    <div className="App">
      <Header user={user} onAuth={() => setShowAuth(true)} onLogout={() => setUser(null)} />

      <HeroSection 
        posts={posts} 
        supportMessage={supportMessage}
        motivationalImage={motivationalImage}
        onShowCreateForm={() => setShowCreateForm(true)}
        user={user}
      />

      {showCreateForm && (
        <CreatePostForm 
          onSubmit={handleCreatePost}
          onClose={() => setShowCreateForm(false)}
        />
      )}

      <PostsSection 
        posts={posts}
        showComments={showComments}
        comments={comments}
        newComment={newComment}
        onSupport={handleSupport}
        onToggleComments={toggleComments}
        onCommentChange={setNewComment}
        onCommentSubmit={handleComment}
        user={user}
      />

      <EmergencySection />

      <Footer />
    </div>
  );
}

// Composants s√©par√©s pour plus de clart√©
function LoadingScreen() {
  return (
    <div className="loading-screen">
      <div className="spinner">‚è≥</div>
      <h2>Chargement en cours</h2>
      <p>Pr√©paration de votre espace de soutien...</p>
    </div>
  );
}

function ErrorScreen({ error, onRetry }) {
  return (
    <div className="error-screen">
      <div className="error-icon">‚ùå</div>
      <h2>Erreur de connexion</h2>
      <p>{error}</p>
      <button onClick={onRetry} className="btn-retry">R√©essayer</button>
    </div>
  );
}

function Header({ user, onAuth, onLogout }) {
  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo">
            <span className="logo-emoji">ü§ó</span>
            <h1>Soutien Ensemble</h1>
          </div>
          <div className="header-actions">
            {user ? (
              <div className="user-info">
                <span>üëã Bonjour, {user.username}</span>
                <button onClick={onLogout} className="btn-logout">D√©connexion</button>
              </div>
            ) : (
              <button onClick={onAuth} className="btn-login">Connexion</button>
            )}
          </div>
        </div>
        <p className="tagline">Vous n'√™tes pas seul</p>
      </div>
    </header>
  );
}

function HeroSection({ posts, supportMessage, motivationalImage, onShowCreateForm, user }) {
  return (
    <section className="hero">
      <div className="container">
        <div className="hero-content">
          <h2>Espace d'expression et de soutien</h2>
          <p>Partagez vos sentiments en toute s√©curit√©</p>

          {supportMessage && (
            <div className="support-message">
              <span className="message-icon">üí´</span>
              {supportMessage}
            </div>
          )}

          {motivationalImage && (
            <div className="motivational-image">
              <img src={motivationalImage.url} alt={motivationalImage.alt} />
              <small>üì∏ {motivationalImage.credit}</small>
            </div>
          )}

          {user ? (
            <button onClick={onShowCreateForm} className="btn-create">
              ‚úçÔ∏è Cr√©er une publication
            </button>
          ) : (
            <button onClick={onShowCreateForm} className="btn-create">
              üîê Se connecter pour participer
            </button>
          )}

          <div className="hero-stats">
            <div className="stat">
              <span className="stat-number">{posts.length}</span>
              <span className="stat-label">Publications</span>
            </div>
            <div className="stat">
              <span className="stat-number">
                {posts.reduce((sum, post) => sum + post.supportCount, 0)}
              </span>
              <span className="stat-label">Soutiens</span>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

function CreatePostForm({ onSubmit, onClose }) {
  const [formData, setFormData] = useState({
    title: '', content: '', mood: 'triste', author: 'Anonyme'
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <section className="create-post-section">
      <div className="container">
        <div className="create-post-card">
          <div className="form-header">
            <h3>‚úçÔ∏è Partager mes sentiments</h3>
