import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // 🎯 CHARGER LES POSTS DEPUIS L'API
  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:5000/api/posts');

      if (!response.ok) {
        throw new Error('Erreur de chargement des publications');
      }

      const data = await response.json();

      if (data.success) {
        setPosts(data.posts);
      } else {
        throw new Error(data.message || 'Erreur inconnue');
      }

    } catch (error) {
      console.error('Erreur:', error);
      setError('Impossible de charger les publications');
    } finally {
      setLoading(false);
    }
  };

  // 🎯 SOUTENIR UN POST VIA L'API
  const handleSupport = async (postId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/posts/${postId}/support`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        }
      });

      const data = await response.json();

      if (data.success) {
        // Mettre à jour le compteur localement
        setPosts(posts.map(post => 
          post.id === postId 
            ? { ...post, supportCount: data.supportCount }
            : post
        ));

        alert('🤗 ' + data.message);
      } else {
        alert('❌ ' + data.message);
      }
    } catch (error) {
      console.error('Erreur:', error);
      alert('❌ Erreur lors de l\'envoi du soutien');
    }
  };

  const getMoodEmoji = (mood) => {
    const emojis = {
      triste: '😔',
      anxieux: '😰',
      espoir: '✨',
      calme: '😌',
      colère: '😠',
      mélancolique: '🌧️'
    };
    return emojis[mood] || '😊';
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('fr-FR', {
      day: 'numeric',
      month: 'short',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="loading-screen">
        <div className="spinner">⏳</div>
        <h2>Chargement en cours</h2>
        <p>Connexion à l'API en cours...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-screen">
        <div className="error-icon">❌</div>
        <h2>Erreur de connexion</h2>
        <p>{error}</p>
        <button onClick={fetchPosts} className="btn-retry">
          Réessayer
        </button>
        <div className="help-text">
          <p>💡 Assurez-vous que le serveur backend est démarré sur le port 5000</p>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="header">
        <div className="container">
          <div className="logo">
            <span className="logo-emoji">🤗</span>
            <h1>Soutien Ensemble</h1>
          </div>
          <p className="tagline">Vous n'êtes pas seul</p>
        </div>
      </header>

      <section className="hero">
        <div className="container">
          <div className="hero-content">
            <h2>✅ Connecté à l'API !</h2>
            <p>Le frontend communique maintenant avec le backend. Les données sont réelles.</p>

            <div className="hero-stats">
              <div className="stat">
                <span className="stat-number">{posts.length}</span>
                <span className="stat-label">Publications</span>
              </div>
              <div className="stat">
                <span className="stat-number">
                  {posts.reduce((sum, post) => sum + post.supportCount, 0)}
                </span>
                <span className="stat-label">Soutiens</span>
              </div>
            </div>
          </div>
        </div>
      </section>

      <section className="posts-section">
        <div className="container">
          <div className="section-header">
            <h3>Dernières publications</h3>
            <button className="btn-refresh" onClick={fetchPosts}>
              🔄 Actualiser
            </button>
          </div>

          <div className="posts-grid">
            {posts.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">📝</div>
                <h4>Aucune publication pour le moment</h4>
                <p>Soyez le premier à partager vos sentiments</p>
              </div>
            ) : (
              posts.map(post => (
                <div key={post.id} className="post-card">
                  <div className="post-header">
                    <div className="post-author">
                      <span className="author-avatar">
                        {post.author === 'Anonyme' ? '🕵️' : '👤'}
                      </span>
                      <span className="author-name">{post.author}</span>
                    </div>
                    <div className="post-mood">
                      <span className="mood-emoji">{getMoodEmoji(post.mood)}</span>
                      <span className="mood-text">{post.mood}</span>
                    </div>
                  </div>

                  <div className="post-content">
                    <h4 className="post-title">{post.title}</h4>
                    <p className="post-text">{post.content}</p>
                  </div>

                  <div className="post-footer">
                    <div className="post-meta">
                      <span className="post-date">
                        {formatDate(post.createdAt)}
                      </span>
                    </div>
                    <div className="post-actions">
                      <button 
                        onClick={() => handleSupport(post.id)}
                        className="btn-support"
                        title="Envoyer un soutien"
                      >
                        <span className="support-emoji">🤗</span>
                        Soutenir ({post.supportCount})
                      </button>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </section>

      <footer className="footer">
        <div className="container">
          <p>🤗 Frontend + Backend connectés - Plateforme opérationnelle</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
